function init(){$.get("./php/CYShop.php",{},function(e){let t=[];t=3;let o=order(e,"price"),r=order(e,"price",1);new Vue({el:"#prodRender",data:{prodList:e,prodAmount:e.length,pagesBtn:t,pagesBtnIsFirst:!0},methods:{orderOnChange(e){switch(parseInt(e.target.value)){case 1:case 2:break;case 3:this.prodList=o,this.prodAmount=o.length;break;case 4:this.prodList=r,this.prodAmount=r.length;break;default:console.log(`onchange | ${e.target.value}`)}},pageIsActive(e){$(e.target).parent().children().removeClass("pages_active"),$(e.target).addClass("pages_active")}},computed:{}})},"json"),Vue.component("prod-component",{props:["pname","price","author"],methods:{clickToDetail(e){e.stopPropagation(),localStorage.prodName="公路車",window.open("./CYProd.html","_self")},clickToAdd(e){localStorage["cart-prodName"]="越野車",$(".prod_cart").click(function(e){e.stopPropagation();let t=$(this).parent().parent().clone(),o=$(this).parent().parent().offset();$("body").append(t),t.offset(o),t.css({width:$(this).parent().parent().css("width")}),t.addClass("addToCart"),TweenMax.to(".addToCart",.5,{x:$(window).width()-$(t).offset().left-300,y:-$(t).offset().top+$(window).scrollTop()-130,scale:0,onComplete:()=>{$(".addToCart").remove()},ease:Power2.easeIn})})}},template:'\n            <li class="prod col-5 col-lg-4 col-xl-3" @click.stop="clickToDetail">\n                <div class="prod-shell">\n                    <div class="prod-pic">\n                        <img src=./images/TCR_SLR_M01.png>\n                    </div>\n                    <h3 class="prod-title">{{author}}</h3>\n                    <p class="prod-context">{{pname}}</p>\n                    <p class="prod-money">${{price}}</p>\n                    <div class="prodIcon">\n                        <p class="prod_like"></p>\n                        <p class="prod_cart" @click.stop="clickToAdd"></p>\n                    </div>\n                </div>\n            </li>\n        '})}let order=function(e,t,o=0){let r=e.slice(),a=r.length;if(0==o){for(var n=0;n<a;n++)for(var p=0;p<a-1-n;p++)if(parseInt(r[p][t])>parseInt(r[p+1][t])){let e=r[p];r[p]=r[p+1],r[p+1]=e}}else for(n=0;n<a;n++)for(p=0;p<a-1-n;p++)if(parseInt(r[p][t])<parseInt(r[p+1][t])){let e=r[p];r[p]=r[p+1],r[p+1]=e}return r},typeFilter=function(e,t){let o=object.slice(),r=o.length,a=[];for(let n=0;n<r;n++)parseInt(o[n][e])==t&&a.push(o[n]);return a},allBike=function(){let e=object.slice(),t=e.length,o=[];for(let r=0;r<t;r++)4!==parseInt(e[r].prodType)&&5!==parseInt(e[r].prodType)&&o.push(e[r]);return o};window.addEventListener("load",init,!1);